if(!$is_liked && $contains_hide && $id_author !== MID && !allowedTo('hide_see_through')) {
    loadSource('Subs-BBC');
    $context['hide_like']['body'] = parse_bbc($body, 'post', ['show_hide_like' => true]);
}

if(!isset($context['hide_like']))
    log_error('Missing my $context[\'hide_like\']!');
else
